   function onSubmit() {
  var callerId = g_form.getValue('caller_id'); // get the value of the caller_id field
  var shortDescription = g_form.getValue('short_description'); // get the value of the short_description field

  if (callerId && shortDescription) { // check if the caller_id and short_description fields have values
    // do something here, such as show a confirmation message or perform additional validation
    // you can use the g_form.addInfoMessage() or g_form.addErrorMessage() functions to display messages to the user
  }
  else {
    // if either field is empty, show an error message to the user
    g_form.addErrorMessage('Please fill in both the Caller and Short Description fields before submitting.');
    return false; // return false to prevent the form from submitting
  }
}

